import time
import unittest
from collections import defaultdict

deliveries = {}
indDirections = {}

def houses_delivered(directions, deliveryPersons):
    # Takes str of cardinal directions (^v><) and int of delivery persons (1,2,3..) and returns int of unique coords that are passed by delivery persons starting from 0,0
    # Typecheck inputs. Additional str checking takes place in deliver_pizzas to reduce runtime
    if isinstance(directions, str) == False:
        return -1
    if isinstance(deliveryPersons, int) == False or deliveryPersons <= 0:
        return -1

    # Clear dictionary from previous use
    deliveries.clear()
    indDirections.clear()

    # Initialize each key (delivery person) with empty "" str value
    initialize_deliveryperson_dict(deliveryPersons)

    # Split directions into n strings where n = # of delivery persons and append to each key (delivery person) in indDirections{} dict
    split_directions(directions, deliveryPersons)

    # Add home coords (0,0) to dictionary of deliveries
    append_coords_to_dict(0, 0)

    # Iterate over delivery persons and add each visited house coordinates to deliveries{} dict. Return -1 for directions containing incorrect input
    for key in indDirections:
        if deliver_pizzas(indDirections[key]) == -1:
            return -1

    # Remove duplicate house coordinates in deliveries by creating set() and get len (unique houses delivered)
    uniqueHouseCount = count_unique_coords(deliveries)

    print(deliveryPersons, ' delivery persons, ', uniqueHouseCount, ' houses receive at least one pizza')
    return uniqueHouseCount

def initialize_deliveryperson_dict(deliveryPersons):
    for person in range(deliveryPersons):
        indDirections[person] = ""

def split_directions(directions, deliveryPersons):
    currPerson = 0
    for i, dir in enumerate(directions):
        indDirections[currPerson] = f"{indDirections[currPerson]}{dir}"
        if currPerson < deliveryPersons - 1:
            currPerson += 1
        else:
            currPerson = 0
    # print('inddir: ', indDirections)

def append_coords_to_dict(x, y):
    # Takes x and y coordinates and assigns as key/value pair to deliveries dict
    if x in deliveries:  # check if key (x coord) is in dict, append value (y coord)
        deliveries[x].append(y)
    else:  # if key (x coord) not in dict, create and assign value (y coord)
        deliveries[x] = [y]

def deliver_pizzas(str):
    # Iterate through string, change coordinates after each character, call append function to add coordinates to deliveries{}
    coords = [0, 0]
    for i, dir in enumerate(str):
        if dir == '<': coords[0] -= 1
        elif dir == '>': coords[0] += 1
        elif dir == 'v': coords[1] -= 1
        elif dir == '^': coords[1] += 1
        else: # Indicate non-directional characters to return -1 to parent function to return -1 for solution
            return -1;

        append_coords_to_dict(coords[0], coords[1])
        # print(dir, ' ', coords);

def count_unique_coords(dict):
    uniqueHouseCount = 0
    for key in dict:
        values_set = set(deliveries[key])
        uniqueHouseCount += len(values_set)
        # print('unique y=', key, 'x=', values_set)
    return uniqueHouseCount

