    def test_house_delivered(self):
        num_runs = 1
        function_runtimes = defaultdict(float)

        for _ in range(num_runs):
            for directions, deliveryPersons, expected in self.test_cases:
                for h_d in self.test_functions:
                    start = time.perf_counter()
                    assert (h_d(directions, deliveryPersons) == expected), f"{h_d.__name__} failed for value: {directions}"
                    function_runtimes[h_d.__name__] += (time.perf_counter() - start) * 1000
        print(f"\n{num_runs} runs")
        for function_name, runtime in function_runtimes.items():
            print(f"{function_name}: {runtime:.1f}ms")


